
public class 문제4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 3;
		a = a+1;//4
		System.out.println(a);//4
		a+=1;//5
		System.out.println(a);//5
		a-=1;//4
		System.out.println(a);//4
		a*=2;//8
		System.out.println(a);//8
		a/=2;//4
		System.out.println(a);//4
		a%=2;//0
		System.out.println(a);//0

	}

}
